        try {
            document.getElementsByClassName("author-info__name")[0].style.textShadow = "";
            document.getElementsByClassName("author-info__description")[0].style.textShadow = "";
        } catch {

        }
    }
}

// 开启计时器
window.onload = setInterval(changeColor, 1200);

