    background: transparent !important;
  }
  #page-header::before {
    background: transparent !important;
  }

  /* 夜间模式遮罩透明 */
  [data-theme="dark"] #footer::before {
    background: transparent !important;
  }
  [data-theme="dark"] #page-header::before {
    background: transparent !important;
  }

  /* 翻页按钮居中 */
#pagination {
    width: 100%;
    margin: auto;
  }

  /* 一级菜单居中 */
#nav .menus_items {
    position: absolute !important;
    width: fit-content !important;
    left: 50% !important;
    transform: translateX(-50%) !important;
  }

/* 夜间模式菜单栏发光字 */
[data-theme="dark"] #nav .site-page,
[data-theme="dark"] #nav .menus_items .menus_item .menus_item_child li a {
  text-shadow: 0 0 2px var(--theme-color) !important;
}

/* 手机端适配 */
[data-theme="dark"] #sidebar #sidebar-menus .menus_items .site-page {
  text-shadow: 0 0 2px var(--theme-color) !important;
}

/* 闪烁变动颜色连续渐变 */
#site-name,
#site-title,
#site-subtitle,
#post-info,
.author-info__name,
.author-info__description {
  transition: text-shadow 1s linear !important;
}

/* 背景宇宙星光  */
#universe{
    display: block;
    position: fixed;
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    /* 这个是调置顶的优先级的，-1在文章页下面，背景上面，个人推荐这种 */
    z-index: -1;
  }

  /* 鼠标样式 *//* 鼠标样式 */
  #cursor {
    position: fixed;
    width: 16px;
    height: 16px;
    /* 这里改变跟随的底色 */
    background: var(--theme-color, #39c5bb); /* 添加一个回退颜色 */
    border-radius: 8px;
    opacity: 0.25;
    z-index: 10086;
    pointer-events: none;
    transition: 0.2s ease-in-out;
    /* 更新 transition property，包含 transform 以平滑缩放 */
    transition-property: background-color, opacity, transform;
  }

  #cursor.hidden {
    opacity: 0;
  }

  #cursor.hover {
    opacity: 0.1;
    transform: scale(2.5);
    -webkit-transform: scale(2.5);
    -moz-transform: scale(2.5);
    -ms-transform: scale(2.5);
    -o-transform: scale(2.5);
  }

  #cursor.active {
    opacity: 0.5;
    transform: scale(0.5);
    -webkit-transform: scale(0.5);
    -moz-transform: scale(0.5);
    -ms-transform: scale(0.5);
    -o-transform: scale(0.5);
  }

  @media (max-width: 768px) {
    #cursor {
      display: none !important;
    }
  }

/*哔哩哔哩视频适配*/
.aspect-ratio {
    position: relative;
    width: 90%;
    height: auto;
    padding-bottom: 75%;
    margin: 3% auto;
    text-align: center;
  }
  .aspect-ratio iframe {
    position: absolute;
    width: 100%;
    height: 86%;
    left: 0;
    top: 0;
  }
/* 文章页H1-H6图标样式效果 */
/* 控制风车转动速度 4s那里可以自己调节快慢 */
/* 设置标题容器为相对定位，防止图标出界 *//* 针对 beautify 插件插入的图标：统一设置动画 */
/* 风车旋转动画：4秒一圈 */
/* 设置标题容器为相对定位，防止图标出界 */
/* 风车旋转动画：4秒一圈 */
@keyframes windmill-spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(-1turn);
  }
}

/* 风车默认样式：作为 H1~H6 左侧图标 */
h1::before,
h2::before,
h3::before,
h4::before,
h5::before,
h6::before {
  content: '';
  display: inline-block;
  margin-right: 0.5rem;
  animation: windmill-spin 4s linear infinite;
}

/* 风车颜色 + 定位微调 */
#content-inner.layout h1::before {
  color: #d1a38d;  /* 橙赭色（按钮 hover） */
  font-size: 1.3rem;
  margin-left: -1.55rem;
  margin-top: -0.23rem;
}
#content-inner.layout h2::before {
  color: #9ebc9d;  /* 奶油绿（主色） */
  font-size: 1.1rem;
  margin-left: -1.35rem;
  margin-top: -0.12rem;
}
#content-inner.layout h3::before {
  color: #7a7267;  /* 棕灰色（链接/元信息色） */
  font-size: 0.95rem;
  margin-left: -1.22rem;
  margin-top: -0.09rem;
}
#content-inner.layout h4::before {
  color: #9ebc9d;
  font-size: 0.8rem;
  margin-left: -1.05rem;
}
#content-inner.layout h5::before {
  color: #d1a38d;
  font-size: 0.7rem;
  margin-left: -0.9rem;
}
#content-inner.layout h6::before {
  color: #7a7267;
  font-size: 0.66rem;
  margin-left: -0.9rem;
}

/* Hover 状态：风车颜色变为主题色，转慢一点 */
#content-inner.layout h1:hover::before,
#content-inner.layout h2:hover::before,
#content-inner.layout h3:hover::before,
#content-inner.layout h4:hover::before,
#content-inner.layout h5:hover::before,
#content-inner.layout h6:hover::before {
  color: var(--theme-color);
  animation: windmill-spin 6s linear infinite;
}

/* Hover 标题文字本身也变色 */
#content-inner.layout h1:hover,
#content-inner.layout h2:hover,
#content-inner.layout h3:hover,
#content-inner.layout h4:hover,
#content-inner.layout h5:hover,
#content-inner.layout h6:hover {
  color: var(--theme-color);
}

@media screen and (max-width: 768px) {
  #content-inner.layout h1::before,
  #content-inner.layout h2::before,
  #content-inner.layout h3::before,
  #content-inner.layout h4::before,
  #content-inner.layout h5::before,
  #content-inner.layout h6::before {
    margin-left: -0.5rem;
  }
}

