function debounce(fn, delay) {
    let timer = null;
    return function () {
      clearTimeout(timer);
      timer = setTimeout(fn, delay);
    };
  }

  // å¤åˆ¶æé†’ï¼ˆElementUIï¼‰
  const notifyCopy = debounce(() => {
    ELEMENT.Notification({
      title: 'å¤åˆ¶æˆåŠŸ',
      message: 'è‹¥è¦è½¬è½½è¯·ä¿ç•™åŸæ–‡é“¾æ¥ï¼',
      position: 'top-left',
      offset: 50,
      duration: 5000
    });
  }, 300);

  document.addEventListener("copy", notifyCopy);

  // F12 æé†’ï¼ˆä½†ä¸å±è”½ï¼‰
  const notifyDev = debounce(() => {
    ELEMENT.Notification({
      title: 'ä½ å·²è¢«å‘ç°',
      message: 'æ‰’æºè¦è®°ä½è¦éµå¾ªGPLåè®®ï¼',
      position: 'top-left',
      offset: 50,
      duration: 5000
    });
  }, 300);

  document.onkeydown = function (e) {
    if (
      e.keyCode === 123 || // F12
      (e.ctrlKey && e.shiftKey && [74, 73, 67].includes(e.keyCode)) || // Ctrl+Shift+J/I/C
      (e.ctrlKey && e.keyCode === 85) // Ctrl+U
    ) {
      notifyDev();
    }
  };

