// Â¶ÇÊûúÂΩìÂâçÈ°µÊúâËØÑËÆ∫Â∞±ÊâßË°åÂáΩÊï∞
if (document.getElementById('post-comment')) owoBig();
// Ë°®ÊÉÖÊîæÂ§ß
function owoBig() {
    let flag = 1, // ËÆæÁΩÆËäÇÊµÅÈòÄ
        owo_time = '', // ËÆæÁΩÆËÆ°Êó∂Âô®
        m = 3; // ËÆæÁΩÆÊîæÂ§ßÂÄçÊï∞
    // ÂàõÂª∫ÁõíÂ≠ê
    let div = document.createElement('div'),
        body = document.querySelector('body');
    // ËÆæÁΩÆID
    div.id = 'owo-big';
    // ÊèíÂÖ•ÁõíÂ≠ê
    body.appendChild(div)

    // ÊûÑÈÄ†observer
    let observer = new MutationObserver(mutations => {

        for (let i = 0; i < mutations.length; i++) {
            let dom = mutations[i].addedNodes,
                owo_body = '';
            if (dom.length == 2 && dom[1].className == 'OwO-body') owo_body = dom[1];
            // Â¶ÇÊûúÈúÄË¶ÅÂú®ËØÑËÆ∫ÂÜÖÂÆπ‰∏≠ÂêØÁî®Ê≠§ÂäüËÉΩËØ∑Ëß£Èô§‰∏ãÈù¢ÁöÑÊ≥®Èáä
            // else if (dom.length == 1 && dom[0].className == 'tk-comment') owo_body = dom[0];
            else continue;

            // Á¶ÅÁî®Âè≥ÈîÆÔºàÊâãÊú∫Á´ØÈïøÊåâ‰ºöÂá∫Áé∞Âè≥ÈîÆËèúÂçïÔºå‰∏∫‰∫Ü‰ΩìÈ™åÁªôÁ¶ÅÁî®ÊéâÔºâ
            if (document.body.clientWidth <= 768) owo_body.addEventListener('contextmenu', e => e.preventDefault());
            // Èº†Ê†áÁßªÂÖ•
            owo_body.onmouseover = (e) => {
                    if (flag && e.target.tagName == 'IMG') {
                        flag = 0;
                        // ÁßªÂÖ•300ÊØ´ÁßíÂêéÊòæÁ§∫ÁõíÂ≠ê
                        owo_time = setTimeout(() => {
                            let height = e.path[0].clientHeight * m, // ÁõíÂ≠êÈ´ò
                                width = e.path[0].clientWidth * m, // ÁõíÂ≠êÂÆΩ
                                left = (e.x - e.offsetX) - (width - e.path[0].clientWidth) / 2, // ÁõíÂ≠ê‰∏éÂ±èÂπïÂ∑¶ËæπË∑ùÁ¶ª
                                top = e.y - e.offsetY; // ÁõíÂ≠ê‰∏éÂ±èÂπïÈ°∂ÈÉ®Ë∑ùÁ¶ª

                            if ((left + width) > body.clientWidth) left -= ((left + width) - body.clientWidth + 10); // Âè≥ËæπÁºòÊ£ÄÊµãÔºåÈò≤Ê≠¢Ë∂ÖÂá∫Â±èÂπï
                            if (left < 0) left = 10; // Â∑¶ËæπÁºòÊ£ÄÊµãÔºåÈò≤Ê≠¢Ë∂ÖÂá∫Â±èÂπï
                            // ËÆæÁΩÆÁõíÂ≠êÊ†∑Âºè
                            div.style.cssText = `display:flex; height:${height}px; width:${width}px; left:${left}px; top:${top}px;`;
                            // Âú®ÁõíÂ≠ê‰∏≠ÊèíÂÖ•ÂõæÁâá
                            div.innerHTML = `<img src="${e.target.src}">`
                        }, 300);
                    }
                };
            // Èº†Ê†áÁßªÂá∫ÈöêËóèÁõíÂ≠ê
            owo_body.onmouseout = () => { div.style.display = 'none', flag = 1, clearTimeout(owo_time); }
        }

    })
    observer.observe(document.getElementById('post-comment'), { subtree: true, childList: true }) // ÁõëÂê¨ÁöÑ ÂÖÉÁ¥† Âíå ÈÖçÁΩÆÈ°π
}

