function debounce(fn, delay) {
    let timer = null;
    return function () {
      clearTimeout(timer);
      timer = setTimeout(fn, delay);
    };
  }

  // Â§çÂà∂ÊèêÈÜíÔºàElementUIÔºâ
  const notifyCopy = debounce(() => {
    ELEMENT.Notification({
      title: 'Â§çÂà∂ÊàêÂäü',
      message: 'Ëã•Ë¶ÅËΩ¨ËΩΩËØ∑‰øùÁïôÂéüÊñáÈìæÊé•ÔºÅ',
      position: 'top-left',
      offset: 50,
      duration: 5000
    });
  }, 300);

  document.addEventListener("copy", notifyCopy);

  // F12 ÊèêÈÜíÔºà‰ΩÜ‰∏çÂ±èËîΩÔºâ
  const notifyDev = debounce(() => {
    ELEMENT.Notification({
      title: '‰Ω†Â∑≤Ë¢´ÂèëÁé∞',
      message: 'ÊâíÊ∫êË¶ÅËÆ∞‰ΩèË¶ÅÈÅµÂæ™GPLÂçèËÆÆÔºÅ',
      position: 'top-left',
      offset: 50,
      duration: 5000
    });
  }, 300);

  document.onkeydown = function (e) {
    if (
      e.keyCode === 123 || // F12
      (e.ctrlKey && e.shiftKey && [74, 73, 67].includes(e.keyCode)) || // Ctrl+Shift+J/I/C
      (e.ctrlKey && e.keyCode === 85) // Ctrl+U
    ) {
      notifyDev();
    }
  };

