:root {
  --trans-light: rgba(255, 255, 255, 0.88);  /* ÊµÖËâ≤ÈÄèÊòéËÉåÊôØ */
  --trans-dark: rgba(25, 25, 25, 0.88);     /* Ê∑±Ëâ≤ÈÄèÊòéËÉåÊôØ */
  --border-style: 0px solid rgb(169, 169, 169); /* ËæπÊ°ÜÊ†∑Âºè */
  --backdrop-filter: blur(5px) saturate(150%); /* ËÉåÊôØÊ®°Á≥äÊïàÊûú */
}

/* Ëá™ÂÆö‰πâÂõæÊ†á */
.iconfont {
  font-family: "iconfont" !important;
  font-size: 3em; /* ËøôÈáåÂèØ‰ª•Ëá™ÂÆö‰πâÂõæÊ†áÂ§ßÂ∞è */
  font-style: normal;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* È¶ñÈ°µÊñáÁ´†Âç°Áâá */
#recent-posts .recent-post-item {
  background: var(--trans-light);
  backdrop-filter: var(--backdrop-filter); /* ËÉåÊôØÊ®°Á≥äÊïàÊûú */
  border-radius: 25px;
  border: var(--border-style);
}

/* È¶ñÈ°µ‰æßÊ†èÂç°Áâá */
#aside-content .card-widget {
  background: var(--trans-light);
  backdrop-filter: var(--backdrop-filter);
  border-radius: 18px;
  border: var(--border-style);
}

/* ÊñáÁ´†È°µ„ÄÅÂΩíÊ°£È°µ„ÄÅÊôÆÈÄöÈ°µÈù¢ */
div#post,
div#page,
div#archive {
  background: var(--trans-light);
  backdrop-filter: var(--backdrop-filter);
  border: var(--border-style);
  border-radius: 20px;
}
/* ÁΩÆÈ°∂ÊªöÂä®Ê†èÁîµËÑëÁ´ØÊ†∑ÂºèÔºåÊâãÊú∫Á´ØÁî®ÈªòËÆ§ÁöÑ */
@media screen and (min-width: 600px) {
  .blog-slider {
    height: 260px !important;
  }
  .blog-slider__img {
    width: 350px !important;
    height: 100% !important;
  }
}

/* ÁΩÆÈ°∂ÊªöÂä®ÂúÜËßí */
.blog-slider__img img {
  border-radius: 20px !important;
}

/* ÂØºËà™Ê†è */
#page-header.nav-fixed #nav {
  background: rgba(255, 255, 255, 0.75);
  backdrop-filter: var(--backdrop-filter);
}

/* Â§úÈó¥Ê®°ÂºèÂØºËà™Ê†è */
[data-theme="dark"] #page-header.nav-fixed #nav {
  background: rgba(0, 0, 0, 0.7) !important;
}

/* Â§úÈó¥Ê®°ÂºèÈÅÆÁΩ© */
[data-theme="dark"] #recent-posts .recent-post-item,
[data-theme="dark"] #aside-content .card-widget,
[data-theme="dark"] div#post,
[data-theme="dark"] div#archive,
[data-theme="dark"] div#page {
  background: var(--trans-dark);
