        this.cursor.classList.remove("active");
        this.pos = {curr: null, prev: null};
        this.pt = []; // ÊÅ¢Â§çÊúÄÂàùÁöÑ pt Ê∏ÖÁêÜÈÄªËæë

        this.create();
        this.init();
        this.render();
    }

    init() {
        // === Êñ∞Â¢ûÂáΩÊï∞ÔºöËé∑Âèñ body ÁöÑÊúâÊïà zoom Á∫ßÂà´ ===
        const getZoomLevel = () => {
            let zoom = getStyle(document.body, 'zoom');
            if (!zoom || zoom === 'normal') return 1; // ÈªòËÆ§ zoom ÊòØ 1
            if (zoom.endsWith('%')) {
                return parseFloat(zoom) / 100;
            }
            // Â§ÑÁêÜÊï∞ÂÄºÂΩ¢ÂºèÁöÑ zoom
            return parseFloat(zoom);
        };
        // ========================================

        // ÊÅ¢Â§çÊúÄÂàùÁöÑ mouseover/mouseout ÈÄªËæë (‰æùËµñ pt)
        document.onmouseover  = e => this.pt.includes(e.target.outerHTML) && this.cursor.classList.add("hover");
        document.onmouseout   = e => this.pt.includes(e.target.outerHTML) && this.cursor.classList.remove("hover");

        // === ‰øÆÊîπ mousemove ÈÄªËæëÔºå‰ΩøÁî® zoom ‰øÆÊ≠£ÂùêÊ†á ===
        document.onmousemove  = e => {
            const zoomLevel = getZoomLevel(); // Ëé∑Âèñ zoom Á∫ßÂà´
            // ËÆ°ÁÆó‰øÆÊ≠£ÂêéÁöÑÂùêÊ†áÔºöÂéüÂßãÂùêÊ†áÈô§‰ª• zoom Á∫ßÂà´
            // ÁÑ∂ÂêéÂáèÂéª 8px ÁöÑÂÅèÁßª‰ª•‰ΩøÂÖâÊ†á‰∏≠ÂøÉÂØπÂáÜÊåáÈíà
            const correctedX = (e.clientX / zoomLevel) - 8;
            const correctedY = (e.clientY / zoomLevel) - 8;

            if (this.pos.curr == null) {
                 // Á¨¨‰∏ÄÊ¨°ÁßªÂä®Êó∂ÔºåÁ´ãÂç≥ËÆæÁΩÆÂÖâÊ†á‰ΩçÁΩÆÔºå‰ΩøÁî®‰øÆÊ≠£ÂêéÁöÑÂùêÊ†á
                 this.move(correctedX, correctedY);
                 // ÂàùÂßãÂåñ prev ‰ΩøÁî®‰øÆÊ≠£ÂêéÁöÑÂùêÊ†á
                 this.pos.prev = { x: correctedX, y: correctedY };
            }
            // Êõ¥Êñ∞ curr ‰ΩçÁΩÆÔºå‰ΩøÁî®‰øÆÊ≠£ÂêéÁöÑÂùêÊ†á
            this.pos.curr = {x: correctedX, y: correctedY};

            this.cursor.classList.remove("hidden");
        };
        // ==========================================

        document.onmouseenter = e => this.cursor.classList.remove("hidden");
        document.onmouseleave = e => this.cursor.classList.add("hidden");
        // ‰øùÁïô mousedown Âíå mouseupÔºåÊéßÂà∂ active Áä∂ÊÄÅ
        document.onmousedown  = e => this.cursor.classList.add("active");
        document.onmouseup    = e => this.cursor.classList.remove("active");
    }

    render() {
        // === ÊÅ¢Â§çÊúÄÂàùÁöÑ render ÈÄªËæë (‰∏çÂåÖÂê´È≤ÅÊ£íÊÄßÊ£ÄÊü•) ===
        if (this.pos.prev) {
            // Ê≥®ÊÑèÔºöËøôÈáåÁöÑ lerp ‰ªçÁÑ∂‰ΩøÁî® curr (Â∑≤‰øÆÊ≠£ÁöÑÂùêÊ†á)
            this.pos.prev.x = Math.lerp(this.pos.prev.x, this.pos.curr.x, 0.15);
            this.pos.prev.y = Math.lerp(this.pos.prev.y, this.pos.curr.y, 0.15);
            this.move(this.pos.prev.x, this.pos.prev.y);
        } else {
            this.pos.prev = this.pos.curr;
        }
        // =============================================

        requestAnimationFrame(() => this.render());
    }
}

(() => {
    if (typeof document !== 'undefined') {
        CURSOR = new Cursor();
        // ÈúÄË¶ÅÈáçÊñ∞Ëé∑ÂèñÂàóË°®Êó∂Ôºå‰ΩøÁî® CURSOR.refresh()
    }
})();
